import React from 'react';
import { Link } from "react-router-dom";
import "./ArticleCard.css";

export default function ArticleCard({title,description,slug,date,img,...props}) {
    return (
        <div className="row card" key={props.key}>
            <div className="col col-sm-6 col-xs-12 no-float">
                <h2>{title}</h2>
                <p>{description}<Link to={slug} style={{color:"#26af26"}}> Read More</Link></p>
                <span className="date">{date}</span>
            </div>
            <div className="col col-sm-6 col-xs-12 no-float align">
                <img src={img ? img : window.location.origin + "/default-image.jpg"} height="200px" width="300px"/>
            </div>
        </div>
    )
}
